      "source": [
        "import pandas as pd\n",
        "\n",
        "# Load the raw gene data\n",
        "df = pd.read_csv('/home/user/raw_gene_data.csv')\n",
        "\n",
        "# Calculate the mean expression for each gene, excluding the 'sample' column\n",
        "gene_means = df.drop(columns=['sample']).mean()\n"
      ]
    },
    {
      "cell_type": "code",
      "execution_count": null,
      "metadata": {},
      "outputs": [],
      "source": [
        "# Filter for genes with a mean expression strictly greater than 50\n",
        "filtered_means = gene_means[gene_means > 50]\n"
      ]
    },
    {
      "cell_type": "code",
      "execution_count": null,
      "metadata": {},
      "outputs": [],
      "source": [
        "# Create a DataFrame for the report\n",
        "report_df = pd.DataFrame({\n",
        "    'Gene': filtered_means.index,\n",
        "    'Mean_Expression': filtered_means.values\n",
        "})\n",
        "\n",
        "# Save to CSV without the index\n",
        "report_df.to_csv('/home/user/significant_genes.csv', index=False)\n"
      ]
    }
  ],
