    "import pandas as pd\n",
    "\n",
    "# 1. Data Loading\n",
    "df_store = pd.read_csv('/home/user/store_data.csv')\n",
    "df_region = pd.read_json('/home/user/region_data.json')\n",
    "df_product = pd.read_excel('/home/user/product_data.xlsx')"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "# 2. Data Merging\n",
    "df_merged = pd.merge(df_store, df_region, on='store_id', how='inner')\n",
    "df_merged = pd.merge(df_merged, df_product, on='store_id', how='inner')"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "# 3. Data Cleaning\n",
    "df_merged = df_merged.sort_values(by='store_id', ascending=True)"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "# 4. Export\n",
    "df_merged.to_csv('/home/user/ml_training_data.csv', index=False)"
   ]
  }
