       "source": [
        "df_meta = pd.read_csv('/home/user/plot_metadata.csv')\n",
        "df_yield = pd.read_csv('/home/user/yield_data.csv')\n",
        "df_merged = pd.merge(df_meta, df_yield, on='plot_id', how='inner')"
       ]
      },
      {
       "cell_type": "code",
       "execution_count": null,
       "metadata": {},
       "outputs": [],
       "source": [
        "df_clean = df_merged[(df_merged['daily_temp'] <= 50) & (df_merged['daily_rainfall'] >= 0)]"
       ]
      },
      {
       "cell_type": "code",
       "execution_count": null,
       "metadata": {},
       "outputs": [],
       "source": [
        "summary = df_clean.groupby('fertilizer')[['yield_kg', 'daily_temp']].mean().to_dict(orient='index')\n",
        "with open('/home/user/summary_stats.json', 'w') as f:\n",
        "    json.dump(summary, f)"
       ]
      },
      {
