   "source": [
    "# Gene Expression Analysis\n",
    "This notebook analyzes raw gene expression data, calculates mean expression levels, and filters for significant genes."
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "import pandas as pd\n",
    "\n",
    "# Load the raw gene expression data\n",
    "raw_data_path = '/home/user/raw_gene_data.csv'\n",
    "df = pd.read_csv(raw_data_path)\n",
    "\n",
    "# Calculate the mean expression for each gene, excluding the 'sample' column\n",
    "gene_means = df.drop(columns=['sample']).mean()"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "# Filter the calculated means to keep only genes with a mean expression strictly > 50\n",
    "significant_genes = gene_means[gene_means > 50]"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "# Create a DataFrame from the filtered results with exactly two columns: Gene and Mean_Expression\n",
    "report_df = significant_genes.reset_index()\n",
    "report_df.columns = ['Gene', 'Mean_Expression']\n",
    "\n",
    "# Save this DataFrame to a CSV file without the index\n",
    "output_path = '/home/user/significant_genes.csv'\n",
    "report_df.to_csv(output_path, index=False)"
   ]
  }
