import json

cells = [
    {"cell_type": "code", "execution_count": None, "metadata": {}, "outputs": [], "source": ["import pandas as pd\\n", "import glob\\n", "import json\\n", "from scipy.stats import chi2_contingency"]},
    {"cell_type": "code", "execution_count": None, "metadata": {}, "outputs": [], "source": ["file_pattern = '/home/user/data/campaign_day_*.csv'\\n", "all_files = glob.glob(file_pattern)\\n", "\\n", "data_frames = []\\n", "files_processed = 0\\n", "files_failed = 0\\n", "\\n", "for file in all_files:\\n", "    try:\\n", "        df = pd.read_csv(file)\\n", "        if not {'user_id', 'group', 'converted'}.issubset(df.columns):\\n", "            raise ValueError('Missing columns')\\n", "        data_frames.append(df)\\n", "        files_processed += 1\\n", "        print(f'Processed file {file}')\\n", "    except Exception as e:\\n", "        files_failed += 1\\n", "        print(f'Failed to process file {file}: {e}')\\n", "\\n", "if data_frames:\\n", "    combined_df = pd.concat(data_frames, ignore_index=True)\\n", "else:\\n", "    combined_df = pd.DataFrame(columns=['user_id', 'group', 'converted'])"]},
    {"cell_type": "code", "execution_count": None, "metadata": {}, "outputs": [], "source": ["summary_data = {\\n", "    'control': {'impressions': 0, 'conversions': 0, 'conversion_rate': 0.0},\\n", "    'treatment': {'impressions': 0, 'conversions': 0, 'conversion_rate': 0.0},\\n", "    'files_processed': files_processed,\\n", "    'files_failed': files_failed\\n", "}\\n", "\\n", "if not combined_df.empty:\\n", "    agg = combined_df.groupby('group')['converted'].agg(['count', 'sum']).rename(columns={'count': 'impressions', 'sum': 'conversions'})\\n", "    for group in ['control', 'treatment']:\\n", "        if group in agg.index:\\n", "            imp = int(agg.loc[group, 'impressions'])\\n", "            conv = int(agg.loc[group, 'conversions'])\\n", "            summary_data[group] = {\\n", "                'impressions': imp,\\n", "                'conversions': conv,\\n", "                'conversion_rate': float(conv / imp) if imp > 0 else 0.0\\n", "            }"]},
    {"cell_type": "code", "execution_count": None, "metadata": {}, "outputs": [], "source": ["try:\\n", "    contingency_table = pd.crosstab(combined_df['group'], combined_df['converted'])\\n", "    chi2, p, dof, ex = chi2_contingency(contingency_table)\\n", "    summary_data['chi_square_statistic'] = float(chi2)\\n", "    summary_data['p_value'] = float(p)\\n", "except Exception as e:\\n", "    print('Chi-square test failed:', e)\\n", "    summary_data['chi_square_statistic'] = 0.0\\n", "    summary_data['p_value'] = 1.0"]},
    {"cell_type": "code", "execution_count": None, "metadata": {}, "outputs": [], "source": ["total_matched_files = len(all_files)\\n", "assert files_processed + files_failed == total_matched_files, 'File count mismatch'\\n", "if not combined_df.empty:\\n", "    total_conv_df = int(combined_df['converted'].sum())\\n", "    total_conv_summary = summary_data['control']['conversions'] + summary_data['treatment']['conversions']\\n", "    assert total_conv_summary == total_conv_df, 'Conversions mismatch'\\n", "print('All validations passed!')"]},
    {"cell_type": "code", "execution_count": None, "metadata": {}, "outputs": [], "source": ["with open('/home/user/ab_test_summary.json', 'w') as f:\\n", "    json.dump(summary_data, f, indent=4)\\n", "print('Summary saved to JSON.')"]}
