import time

def get_expression_data(page):
    time.sleep(0.5)
    if page < 1 or page > 20:
        return []
    data = []
    for i in range(10):
        gene_num = (page - 1) * 10 + i
        data.append({
            "gene_id": f"GENE_{gene_num:03d}",
            "expression_level": round(10.5 + page * 0.1 + i * 0.05, 2),
            "sample_id": "S1"
        })
    return data
