   "source": [
    "import pandas as pd\n",
    "import numpy as np\n",
    "\n",
    "np.random.seed(42)\n",
    "categories = ['Electronics', 'Clothing', 'Home']\n",
    "data = {\n",
    "    'item_id': range(1, 101),\n",
    "    'category': np.random.choice(categories, 100),\n",
    "    'price': np.random.uniform(10, 100, 100),\n",
    "    'discount': np.random.uniform(0, 0.3, 100)\n",
    "}\n",
    "df = pd.DataFrame(data)"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "df['discounted_price'] = df['price'] * (1 - df['discount'])\n",
    "summary = df.groupby('category')['discounted_price'].sum().reset_index()"
   ]
  },
  {
   "cell_type": "markdown",
   "metadata": {},
   "source": [
    "The following validation checks ensure data integrity:"
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "assert len(df) == 100, \"Data should have exactly 100 rows\"\n",
    "assert df['discounted_price'].min() >= 0, \"Discounted price should not be negative\"\n",
    "assert np.isclose(summary['discounted_price'].sum(), df['discounted_price'].sum()), \"Total discounted price in summary should match the total in the raw data\""
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "summary.to_csv('/home/user/final_sales_summary.csv', index=False)"
   ]
  }
