   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "df['discounted_price'] = df['price'] * (1 - df['discount'])\n",
    "summary = df.groupby('category')['discounted_price'].sum().reset_index()"
   ]
  },
  {
   "cell_type": "markdown",
   "metadata": {},
   "source": [
    "Validation step to ensure data correctness."
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "assert len(df) == 100, \"Expected exactly 100 rows\"\n",
    "assert df['discounted_price'].min() >= 0, \"Minimum discounted price should be >= 0\"\n",
    "assert np.isclose(summary['discounted_price'].sum(), df['discounted_price'].sum()), \"Total discounted price mismatch\""
   ]
  },
  {
   "cell_type": "code",
   "execution_count": null,
   "metadata": {},
   "outputs": [],
   "source": [
    "summary.to_csv('/home/user/final_sales_summary.csv', index=False)"
   ]
